import React, { useState, useEffect } from 'react';
import {fetchFollow, deleteFollow, saveFollow} from './api.js';


export default function FollowButton({memberID}){
    const [followers, setFollowers] = useState([])
    const [isFollowers, setIsFollowers] = useState(false)
    useEffect(() => {
        fetchFollow().then((f) => {
          if(f.length !== 0){
            setFollowers(f)
            setIsFollowers(true)
          }
        })

    }, [isFollowers]);
    function addFollower(event){
        saveFollow({
            followerID: event.target.value,
          }).then((newFollower) => {
              setFollowers(followers.concat(newFollower))
              setIsFollowers(true);

        })
    }
    function deleteFollower(event){
        deleteFollow(event.target.value).then (() => {
            const filteredFollowers = followers.filter((f) => {
              return Number(f.followerID) !== event.target.value;
            });
            if(filteredFollowers.length() === 0){
                setIsFollowers(false)
            }

            setFollowers(filteredFollowers);
        });
    }
    return(
        <>  
            {isFollowers ? (followers.map((f) => {
                if(Number(f.followerID) === memberID){
                    return(<button type="button" className="btn btn-outline-danger" value={memberID} onClick={deleteFollower}>Unfollow</button>);
                }else {
                    return(<button type="button" className="btn btn-outline-danger" value={memberID} onClick={addFollower}>Follow</button>);
                }
            })) : (<button type="button" className="btn btn-outline-danger" value={memberID} onClick={addFollower}>Follow</button>)}     
        </>
    );
}
