import React, { useState, useEffect } from 'react';
import {fetchMembers} from './github_api';
import {fetchFollow, deleteFollow, saveFollow} from './api.js';
import MemberModal from './MemberModal';
import ReposModal from './ReposModal';


export default function Members(){
    const [members, setMembers] = useState([])
    const [memberUrl, setMemberUrl] = useState()
    const [repoUrl, setRepoUrl] = useState()
    const [isMemberShown, setIsMemberShown] = useState(false)
    const [isReposShown, setIsReposShown] = useState(false)
    const [followers, setFollowers] = useState([])
    const [noFollowers, setNoFollowers] = useState(true)
    useEffect(() => {
        fetchMembers().then(
        (m) => {
          setMembers(m);
        })
        fetchFollow().then((f) => {
          if(f !== undefined){
            setNoFollowers(false)
            setFollowers(f)
          } else {
            setNoFollowers(true)
          }
        })

    }, []);
    function showMember(event){
        console.log(event.target)
        if(event.target.value){
            setMemberUrl(event.target.value)
        }else{
            setMemberUrl(event.target.alt)
        }
        setIsMemberShown(true);
    }

    function hideMember() {
        setIsMemberShown(false);
    }
    function showRepos(event){
        setRepoUrl(event.target.value)
        setIsReposShown(true);
    }

    function hideRepos() {
        setIsReposShown(false);
    }
    function addFollower(event){
        saveFollow({
            followerID: event.target.value,
          }).then((newFollower) => {
            setNoFollowers(false)
            setFollowers(followers.concat(newFollower));
        })
    }
    function deleteFollower(event){
        deleteFollow(event.target.value).then (() => {
            const filteredFollowers = followers.filter((f) => {
              return f.followerID !== event.target.value;
            });

            setFollowers(filteredFollowers);
        });
    }
    return(
        <>
        {isMemberShown && <MemberModal onClose={hideMember} member = {memberUrl}/>}
        {isReposShown && <ReposModal onClose={hideRepos} repo = {repoUrl}/>}
        <ul className = "list-group mt-5">
            {members.map((member) => {
                return(
                <li key = {member.url} className="list-group-item">
                    <div className = "row">
                        <div className = "col-4">
                            {noFollowers ? <button type="button" className="btn btn-outline-danger" value={member.id} onClick={addFollower}>
                                    <svg width="1em" height="1em" viewBox="0 0 16 16" className="bi bi-plus" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                                        <path fillRule="evenodd" d="M8 4a.5.5 0 0 1 .5.5v3h3a.5.5 0 0 1 0 1h-3v3a.5.5 0 0 1-1 0v-3h-3a.5.5 0 0 1 0-1h3v-3A.5.5 0 0 1 8 4z"/>
                                    </svg>
                            </button> : <></>}
                        </div>
                        <div className="col-4" >
                                <img src={member.avatar_url} className="rounded mr-3" alt={member.url} onClick={showMember}></img>
                                <div id = "followButton"> 

                                </div>
                        </div>
                        <div className="col-4 ">
                            <div>
                                <button className="btn btn-outline-danger mb-4" value={member.url} onClick={showMember}>{member.login}</button>
                            </div>
                            <div>
                                <button className="btn btn-danger text-white" value={member.repos_url} onClick={showRepos}>Repos</button>
                            </div>

                        </div>
                    </div> 
              </li>);
            })}
        </ul>
        </>
    );
}
