import React, { useState, useEffect } from 'react';
import {fetchMembers} from './github_api'
import MemberModal from './MemberModal';


export default function Members(){
    const [members, setMembers] = useState([])
    const [memberUrl, setMemberUrl] = useState()
    const [repoUrl, setRepoUrl] = useState()
    const [isMemberShown, setIsMemberShown] = useState(false)
    const [isReposShown, setIsReposShown] = useState(false)
    useEffect(() => {
        fetchMembers().then(
        (m) => {
          setMembers(m);
        })

    }, []);
    function showMember(event){
        setMemberUrl(event.target.value)
        setIsMemberShown(true);
    }

    function hideMember(event) {
        setIsMemberShown(false);
    }
    function showRepos(event){
        setRepoUrl(event.target.value)
        setIsReposShown(true);
    }

    function hideRepos(event) {
        setIsReposShown(false);
    }
    return(
        <>
        {isMemberShown && <MemberModal onClose={hideMember} member = {memberUrl}/>}
        {isRepoShown && <RepoModal onClose={hideRepos} repos = {repoUrl}/>}
        <ul className = "list-group mt-5 row">
            {members.map((member) => {
                return(
                <li key = {member.url} className="list-group-item">
                    <div className = "row">
                        <div className = "col-3"></div>
                        <div className="col-4" >
                            <img src={member.avatar_url} alt={member.avatar_url} value={member.url} id="thumbnail" onClick={showMember}></img>
                        </div>
                        <div className="col-4 mt-3">
                            <button className="btn btn-outline-danger text-dark" value={member.url} onClick={showMember}>{member.login}</button>
                        </div>
                    </div> 
              </li>);
            })}
        </ul>
        </>
    );
}
